import React, { useEffect, useRef } from 'react';
import Hls from 'hls.js';

interface VideoPlayerProps {
  streamId: string;
}

const VideoPlayer: React.FC<VideoPlayerProps> = ({ streamId }) => {
  const videoRef = useRef<HTMLVideoElement>(null);

  useEffect(() => {
    let hls: Hls | null = null;

    if (Hls.isSupported() && videoRef.current) {
      console.log("Initializing HLS for stream:", streamId);
      hls = new Hls();
      hls.loadSource(`http://127.0.0.1:8000/media/streams/${streamId}/index.m3u8`);
      hls.attachMedia(videoRef.current);

      // Cleanup when component unmounts or streamId changes
      return () => {
        console.log("Destroying HLS instance for stream:", streamId);
        hls?.destroy();
      };
    } else if (videoRef.current?.canPlayType('application/vnd.apple.mpegurl')) {
      // Fallback for Safari or native HLS support
      videoRef.current.src = `http://127.0.0.1:8000/media/streams/${streamId}/index.m3u8`;
    }
  }, [streamId]);

  return (
    <video
      ref={videoRef}
      controls
      autoPlay
      style={{ width: '600px', border: '1px solid black' }}
    />
  );
};

export default VideoPlayer;
