// export default App;
// App.tsx or App.jsx
import React, { useState } from 'react';
import axios from 'axios';
import VideoPlayer from './videoplayer'; 
import './App.css';

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState<string | null>(null);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/', {
        rtsp_url: rtspUrl
      });
      setStreamId(response.data.id);
      alert('Stream started with ID: ' + response.data.id);
    } catch (error) {
      console.error(error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = async () => {
    if (!streamId) return;
    try {
      await axios.post(`http://127.0.0.1:8000/api/streams/${streamId}/stop/`);
      alert('Stream stopped');
      setStreamId(null);
    } catch (error) {
      console.error(error);
      alert('Failed to stop stream');
    }
  };

  return (
    <div className="App">
      <nav className="navbar">
        <div className="logo-container">
          {/* Replace this with your own logo img if you want */}
          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="#007bff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-camera">
            <path d="M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V7a2 2 0 0 1 2-2h4l2-3h6l2 3h4a2 2 0 0 1 2 2z"></path>
            <circle cx="12" cy="13" r="4"></circle>
          </svg>
          <span className="project-name">RTSP Streamer</span>
        </div>
      </nav>

      <div className="content">
        <input
          type="text"
          value={rtspUrl}
          onChange={(e) => setRtspUrl(e.target.value)}
          placeholder="Enter RTSP URL"
          className="rtsp-input"
        />
        <br />

        <button
          onClick={handleStartStream}
          className="btn start-btn"
        >
          Start Stream
        </button>

        <button
          onClick={handleStopStream}
          disabled={!streamId}
          className="btn stop-btn"
        >
          Stop Stream
        </button>

        {streamId && (
          <div style={{ marginTop: '20px' }}>
            <h3>Live Stream</h3>
            <VideoPlayer streamId={streamId} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
