import React, { useState } from 'react';
import axios from 'axios';
import VideoPlayer from './videoplayer'; 
import './App.css';
import logo from './logo.png';  // Put your logo image in src folder or adjust path accordingly

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState(null);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/', {
        rtsp_url: rtspUrl
      });
      setStreamId(response.data.id);
      alert('Stream started with ID: ' + response.data.id);
    } catch (error) {
      console.error(error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = async () => {
    if (!streamId) return;
    try {
      await axios.post(`http://127.0.0.1:8000/api/streams/${streamId}/stop/`);
      alert('Stream stopped');
      setStreamId(null);
    } catch (error) {
      console.error(error);
      alert('Failed to stop stream');
    }
  };

  return (
    <div className="App">
      <nav className="navbar">
        <div className="logo-container">
          <img src={logo} alt="Project Logo" className="logo" />
          <span className="project-name">RTSP Streamer</span>
        </div>
      </nav>

      <div className="content">
        <input
          type="text"
          value={rtspUrl}
          onChange={(e) => setRtspUrl(e.target.value)}
          placeholder="Enter RTSP URL"
          className="input-url"
        />
        <br />

        <button onClick={handleStartStream} className="btn start-btn">
          Start Stream
        </button>

        <button onClick={handleStopStream} disabled={!streamId} className="btn stop-btn">
          Stop Stream
        </button>

        {streamId && (
          <div className="live-stream">
            <h3>Live Stream</h3>
            <VideoPlayer streamId={streamId} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
