import React, { useState } from 'react';
import axios from 'axios';
import VideoPlayer from './videoplayer';
import './App.css';

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState<string | null>(null);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/', {
        rtsp_url: rtspUrl
      });
      setStreamId(response.data.id);
      alert('Stream started with ID: ' + response.data.id);
    } catch (error) {
      console.error(error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = async () => {
    if (!streamId) return;
    try {
      await axios.post(`http://127.0.0.1:8000/api/streams/${streamId}/stop/`);
      alert('Stream stopped');
      setStreamId(null);
    } catch (error) {
      console.error(error);
      alert('Failed to stop stream');
    }
  };

  return (
    <div className="App">
      {/* Header with logo and title */}
      <div className="header">
        <div className="logo">ðŸ“¹</div>
        <h1 className="app-title">RTSP Stream Viewer</h1>
      </div>

      {/* RTSP input */}
      <input
        type="text"
        value={rtspUrl}
        onChange={(e) => setRtspUrl(e.target.value)}
        placeholder="Enter RTSP URL"
        className="url-input"
      />

      {/* Start / Stop buttons */}
      <div className="button-group">
        <button onClick={handleStartStream} className="play-btn play">
          Start Stream
        </button>
        <button onClick={handleStopStream} disabled={!streamId} className="play-btn pause">
          Stop Stream
        </button>
      </div>

      {/* Live Stream Viewer */}
      {streamId && (
        <div style={{ marginTop: '20px' }}>
          <h3>Live Stream</h3>
          <VideoPlayer streamId={streamId} />
        </div>
      )}
    </div>
  );
}

export default App;
