import React, { useState } from 'react';
import axios from 'axios';
import VideoPlayer from './videoplayer'; 
//import Logo from './Logo';
import './App.css';

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState(null);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/', {
        rtsp_url: rtspUrl
      });
      setStreamId(response.data.id);
      alert('Stream started with ID: ' + response.data.id);
    } catch (error) {
      console.error(error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = async () => {
    if (!streamId) return;
    try {
      await axios.post(`http://127.0.0.1:8000/api/streams/${streamId}/stop/`);
      alert('Stream stopped');
      setStreamId(null);
    } catch (error) {
      console.error(error);
      alert('Failed to stop stream');
    }
  };

  return (
    <div className="App">

      {/* Header */}
      <header className="app-header">
     
      </header>

      {/* Controls */}
      <div className="controls">
        <input
          type="text"
          value={rtspUrl}
          onChange={(e) => setRtspUrl(e.target.value)}
          placeholder="Enter RTSP URL"
          className="input-url"
        />
        <button className="btn start-btn" onClick={handleStartStream}>Start Stream</button>
        <button className="btn stop-btn" onClick={handleStopStream} disabled={!streamId}>Stop Stream</button>
      </div>

      {/* Video player */}
      {streamId && (
        <div className="video-wrapper">
          <h3>Live Stream</h3>
          <VideoPlayer streamId={streamId} />
        </div>
      )}
    </div>
  );
}

export default App;
