import React, { useState } from 'react';
import './styles/App.css';
import VideoPlayer from './components/VideoPlayer';
import StreamInput from './components/StreamInput';

const App: React.FC = () => {
  const [streams, setStreams] = useState<string[]>([]);

  const handleAddStream = (url: string) => {
    const httpUrl = `http://localhost:8000/media/streams/${url}/index.m3u8`;
    setStreams((prev) => [...prev, httpUrl]);
  };

  return (
    <div className="App">
      <h1>Multi RTSP Stream Viewer</h1>
      <StreamInput onAddStream={handleAddStream} />
      <div className="grid-container">
        {streams.map((streamUrl, idx) => (
          <VideoPlayer key={idx} url={streamUrl} />
        ))}
      </div>
    </div>
  );
};

export default App;
