import React, { useState } from 'react';
import axios from 'axios';
import VideoPlayer from './videoplayer'; 
import './App.css';

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState<string | null>(null);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/', {
        rtsp_url: rtspUrl
      });
      setStreamId(response.data.id);
      alert('Stream started with ID: ' + response.data.id);
    } catch (error) {
      console.error(error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = async () => {
    if (!streamId) return;
    try {
      await axios.post(`http://127.0.0.1:8000/api/streams/${streamId}/stop/`);
      alert('Stream stopped');
      setStreamId(null);
    } catch (error) {
      console.error(error);
      alert('Failed to stop stream');
    }
  };

  return (
    <div className="App">

       {/* Header */}
      <header className="app-header">
        <span className="logo">ðŸŽ¥</span> {/* Replace with your logo */}
        <h1>RTSP Streamer</h1>
      </header>

      {/* Main content */}
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <input
          type="text"
          value={rtspUrl}
          onChange={(e) => setRtspUrl(e.target.value)}
          placeholder="Enter RTSP URL"
          style={{ padding: '8px', width: '300px', marginBottom: '10px' }}
        />
        <br />

        <button
          onClick={handleStartStream}
          style={{ marginRight: '10px', backgroundColor: 'green', color: 'white', padding: '8px 16px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
        >
          Start Stream
        </button>

        <button
          onClick={handleStopStream}
          style={{
            backgroundColor: 'red',
            color: 'white',
            padding: '8px 16px',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Stop Stream
        </button>

        {streamId && (
          <div style={{ marginTop: '20px' }}>
            <h3>Live Stream</h3>
            <VideoPlayer streamId={streamId} />
          </div>
        )}
      </div>

    </div>
  );
}

export default App;
