import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [rtspUrl, setRtspUrl] = useState('');
  const [streamId, setStreamId] = useState<string>('camera1');
  const [isStreaming, setIsStreaming] = useState(false);

  const handleStartStream = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/streams/start/', {
        stream_id: streamId,
        rtsp_url: rtspUrl
      });
      console.log(response.data);
      alert('Stream started successfully');
      setIsStreaming(true);
    } catch (error) {
      console.error('AxiosError:', error);
      alert('Failed to start stream');
    }
  };

  const handleStopStream = () => {
    // Optional: You can add a stop endpoint in Django to handle this
    alert('Stream stopped (frontend only)');
    setIsStreaming(false);
  };

  return (
    <div className="App">
      <h2>RTSP Streamer</h2>

      <input
        type="text"
        value={rtspUrl}
        onChange={(e) => setRtspUrl(e.target.value)}
        placeholder="Enter RTSP URL"
        style={{ padding: '8px', width: '300px', marginBottom: '10px' }}
      />
      <br />

      <button onClick={handleStartStream} style={{ marginRight: '10px' }}>
        Start Stream
      </button>

      <button onClick={handleStopStream} disabled={!isStreaming}>
        Stop Stream
      </button>

      {isStreaming && (
        <div style={{ marginTop: '20px' }}>
          <h3>Live Stream</h3>
          <video
            src={`http://127.0.0.1:8000/streams/${streamId}/index.m3u8`}
            controls
            autoPlay
            style={{ width: '600px', border: '1px solid black' }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
